import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {useState} from "react";
import {Navigate} from "react-router-dom";
import Editor from "../Editor";
import "./CreatePost.scss"

export default function CreatePost() {

  const [title,setTitle] = useState('');
  const [summary,setSummary] = useState('');
  const [content,setContent] = useState('');
  const [files, setFiles] = useState('');
  const [redirect, setRedirect] = useState(false);

  async function createNewPost(e) {

    const data = new FormData();
    data.set('title', title);
    data.set('summary', summary);
    data.set('content', content);
    data.set('file', files[0]);

    console.log("formdata", data)

    e.preventDefault();

    // const response = await fetch('http://localhost:4000/post', {
    //   method: 'POST',
    //   body: data,
    //   credentials: 'include',
    // });
    // if (response.ok) {
    //   setRedirect(true);
    // }
  }

  if (redirect) {
    return <Navigate to={'/'} />
  }

  return (
    <form className="form-add" onSubmit={createNewPost}>
      <input className="form-add__item" type="title"
             placeholder={'Title'}
             value={title}
             onChange={ev => setTitle(ev.target.value)} />
      <input className="form-add__item" type="summary"
             placeholder={'Summary'}
             value={summary}
             onChange={ev => setSummary(ev.target.value)} />
      <input className="form-add__item" type="file"
             onChange={ev => setFiles(ev.target.files)} />
      <Editor  value={content} onChange={setContent} />
      <button type="submit" className="form-add__item" >Create post</button>
    </form>
  );
}
